#include<bits/stdc++.h>

#define int long long
#define mem(x) memset(x,0,sizeof(x))
#define mod 1000000007

using namespace std;

int read(){
   int s = 0,w = 1;
   char ch = getchar();
   while(ch < '0' || ch > '9'){if(ch == '-')w = -1;ch = getchar();}
   while(ch >= '0' && ch <= '9')s = s * 10 + ch - '0',ch = getchar();
   return s * w;
}

int n,m;

int poww(int b,int p = mod - 2,int m = mod) {
	b %= m;
	int s = 1 % m;
	for(;p;p >>= 1,b = (int)b * b % m)if(p & 1)s = (int)s * b % m;
	return s;
}

int lowbit(int x){
	return x & -x;
}

struct node{
	int tree[1000010];

	void update(int i,int x){
		for(;i <= n;i += lowbit(i)){
			tree[i] += x;
			tree[i] %= mod;
		}
	}

	int query(int x){
		int s = 0;
		for(;x;x -= lowbit(x))s += tree[x],s %= mod;
		return s % mod;
	}
}T[3];

signed main(){
	cin>>n>>m;
	for(int i = 1;i <= n;i ++){
		int x;
		x = read();
		T[1].update(i,x % mod);
		T[2].update(i,x * x % mod);
	}
	while(m --){
		int op,x,y;
		op = read(),x = read(),y = read();
		if(op == 1){
			int num1 = T[1].query(x) - T[1].query(x - 1);
			int num2 = T[2].query(x) - T[2].query(x - 1);
			T[1].update(x,(y - num1) % mod);
			T[2].update(x,(y * y - num2) % mod);
		}
		else {
			int sum = (T[1].query(y) - T[1].query(x - 1)) % mod;
			int sqsum = (T[2].query(y) - T[2].query(x - 1)) % mod;
			int inv = poww(y - x + 1);
			int p = (int)sum * inv % mod;
			int ans = (int)sqsum * inv % mod - (int)p * p % mod;
			ans = (ans % mod + mod) % mod;
			cout<<ans<<endl;
		}
	}
	return 0;
}
