		scanf("%d",&w[i]);
		s[i] = s[i - 1] ^ w[i];
	}
	for(int i = 1;i <= n;i ++) dp[i][1] = s[i];
	for(int j = 2;j <= m;j ++)
	for(int k = 1;k <= n;k ++)
	for(int i = k;i <= n;i ++)
	dp[i][j] = max(dp[i][j],dp[k][j - 1] + (s[i] ^ s[k]));
	printf("%d",dp[n][m]);
	return 0;
}
