        int A = (1ll * x.u * y.r + x.A + y.A) % mod;
        int B = (2ll * x.u * y.A + 1ll * x.u * x.u % mod * y.r + x.B + y.B) % mod;
        int i = 1ll * y.r * (y.r + 1) / 2 % mod;
        int C = (1ll * x.u * i + 1ll * x.r * y.A + 1ll * x.u * x.r % mod * y.r + x.C + y.C) % mod;
