    v.cntv=1;
    auto res=u*(c/b)+f(b-a,b,c%b,c/a,u,v);
    ll ans=res.ans;
    ans+=c/b;
    ans-=1ll*(c/a)*(c/a+1)/2;
    ans+=c/a;
    ans+=1;
