#define F(i, l, r) for (int i = (l); i <= (r); ++i)
#define FF(i, r, l) for (int i = (r); i >= (l); --i)
#define MAX(a, b) ((a) = max(a, b))
#define MIN(a, b) ((a) = min(a, b))

const int N = 55, P = 5557;

struct Mat
{
	int a[N][N];
};
struct Vec
{
	int a[N];
};
void mul_mat(Mat &x, Mat y, int n)
{
	Mat t = x;
	F(i, 1, n) F(j, 1, n)
	{
		x.a[i][j] = 0;
	}
	F(i, 1, n) F(j, 1, n) F(k, 1, n)
	{
		(x.a[i][j] += t.a[i][k] * y.a[k][j]) %= P;
	}
}
void mul_vec(Mat x, Vec &v, int n)
{
	Vec t = v;
	F(i, 1, n)
	{
		v.a[i] = 0;
	}
	F(i, 1, n) F(k, 1, n)
	{
		(v.a[i] += x.a[i][k] * t.a[k]) %= P;
	}
}
void pow_mat(Mat &x, int k, int n)
{
	Mat t = x;
	F(i, 1, n) F(j, 1, n)
	{
		x.a[i][j] = (i == j ? 1 : 0);
	}
	while (k)
	{
		if (k & 1)
		{
			mul_mat(x, t, n);
		}
		mul_mat(t, t, n);
		k >>= 1;
	}
}

int n, m, ti;
struct Edge
{
	int u, v, c;
};
vector<Edge> ori_e;

int solve(int t)
{
	Mat g;
	F(i, 1, 2*n+1) F(j, 1, 2*n+1)
	{
		g.a[i][j] = 0;
