```cpp
#include<bits/stdc++.h>
using namespace std;
const int maxn=310000;
deque<int> Q;//因为头和尾都要插入，所以选用了deque

int s[maxn];//前缀和
int f[maxn];//前n个元素分段后的最佳结果
int main(){
	int n,k;
	char c;
	cin>>n>>k;
	for(int i=1;i<=n;i++){
		cin>>c;
		if(c=='G'){
			s[i]=s[i-1]-1;
		}
		else{
			s[i]=s[i-1]+1;
		}
	}
	Q.push_back(0);
	for(int i=1;i<=n;i++){
		while(!Q.empty()){//检查过期元素 
			int j=Q.front();//最大值 
			if(i-j>k)
				Q.pop_front();
			else
				break; 
		}
		int j=Q.front(); 
		f[i]=f[j]+(s[j]>=s[i]);
		while(!Q.empty()){
			int j=Q.back();
			if(f[j]>f[i]||(f[j]==f[i]&&s[j]>=s[i]))
				Q.pop_back();
			else
				break;
		}
		Q.push_back(i); 
	}
	cout<<f[n];
   return 0;
} 
