	scanf("%d%lld%d",&n,&bas,&op);
	switch(op)
	{
		case 0:{solve0::mainsolve();break;}
		case 1:{solve1::mainsolve();break;}
		case 2:{solve2::mainsolve();break;}
	}return 0;
}
