	{
		for (nn = 1; nn <= n+n; nn<<=1);
		init(nn);

		f[0] = 0;
		for (int i = 1; i < nn; ++i) 
			f[i] = 1ll * qpow(i,i) * qpow(1-y)%mod * y%mod * n%mod * n%mod %mod * iv[i]%mod;

		for (int i = 1; i < nn; ++i) f[i] = (f[i] + mod) % mod;

