				l=mid;
			}
			else{
				r=mid-1;
			}
		}
		ans[o[l+1]]=(l+1^k)&1;
		for(int i=0;i<=l;i++){
			ans[o[i]]=1;
		}
		return;
	}
	int a,b,c;
	a=0,c=1;
	for(int i=2;i<n;i++){
		b=i;
		if(leq(c,b)){
			swap(b,c);
		}
		if(leq(a,b,c)){
			o[cnt++]=a;
			a=c;c=b;
		}
		else{
			ans[b]=0;
		}
	}
	if(leq(c,a)){
		swap(a,c);
	}
	ans[c]=1;
	if(!cnt){
		ans[a]=k^1;
		return;
	}
	o[cnt++]=c;
	int l=0,r=cnt-1;
	reverse(o,o+cnt);
	while(l<r){
		int mid=l+r+1>>1;
		if(mid<cnt-1&&leq(o[0],o[mid],o[mid+1])){
			l=mid;
		}
		else{
