		}
	}
	for(int i=0;i<=l;i++){
		ans[o[i]]=1;
	}//o[l+1] 和 a 未知
	c=o[l+1];
	if(leq(c,a)){
		swap(a,c);
	}
	if(leq(o[0],c,a)){
		ans[c]=1;
