int Calc(char x)
{
	if(x == 'E') return 0;
	if(x == 'S') return 1;
	if(x == 'W') return 2;
	if(x == 'N') return 3;
} 
inline void Init(char s[],int x)
{
	int lens = strlen(s + 1) , root = 0;
	for(int i = 1 ; i <= lens ; i ++)
	{
		int Ind = Calc(s[i]); 
		if(!trie[root][Ind]) 
		{
			trie[root][Ind] = ++ cnt;
			dep[cnt] = dep[root] + 1;
			fa[cnt] = root;
		}
		root = trie[root][Ind];
	}
	pos[x] = root;
}
void Build_Trie_Graph()
{
	queue<int> q;
	for(int i = 0 ; i <= 3 ; i ++) 
	{
		if(trie[0][i])
		{
			fail[trie[0][i]] = 0;
			q.push(trie[0][i]);
		}
	}
	while(!q.empty())
	{
		int u = q.front() ; q.pop();
		for(int i = 0 ; i <= 3 ; i ++)
		{
			if(trie[u][i])
			{
				fail[trie[u][i]] = trie[fail[u]][i];
				q.push(trie[u][i]);
			}
			else trie[u][i] = trie[fail[u]][i];
		}
	}
}
void qry(char s[])
{
	int lens = strlen(s + 1) , root = 0;
	for(int i = 1 ; i <= lens ; i ++)
	{
		int Ind = Calc(s[i]);
		root = trie[root][Ind];
		for(int t = root ; t ; t = fail[t]) Cnt[t] = 1;
	}
}
int Find(int x)
{
	int P = pos[x];
	while(P != 0 && Cnt[P] != 1)
		P = fa[P];
	return dep[P];
}
int main()
{
	scanf("%d %d",&n,&m);
	scanf("%s",ss + 1);
	for(int i = 1 ; i <= m ; i ++)
	{
		scanf("%s",S + 1);
		Init(S , i);
	} 
	fail[0] = dep[0] = 0;
	Build_Trie_Graph();
	qry(ss);
	for(int i = 1 ; i <= m ; i ++) printf("%d\n",Find(i));
	return 0;
}
