bool cmp(string s, long long x)
{
	if(s[0] == '-'){
		if(x >= 0) return 1;
	}else{
		if(x <= 0) return 0;
	}
	char tmp[1000];
	stringstream ss;
	ss << "\%0" << s.length() << "lld";
	sprintf(tmp, ss.str().c_str(), x);
	string sx = tmp;
	if(s[0] == '-') return s > sx;
	else return s < sx;
}
