```cpp
#include<cmath>
#include<cstdio>
#include<cstring>
#include<sstream>
#include<iostream>
using namespace std;
#define debug(x) cout << #x << " = " << x << endl;

long long L, R;

bool cmp(string s, long long x){
	if(s[0] == '-'){
		if(x >= 0) return 1;
	}else{
		if(x <= 0) return 0;
	}
	string::iterator it;
	while(s.length() < 25){
		it = s.begin();
		if(s[0] == '-') it++;
		s.insert(it, '0');
	}
	char tmp[1000];
	stringstream ss;
	ss << "\%0" << s.length() << "lld";
//	debug(ss.str().c_str());
	sprintf(tmp, ss.str().c_str(), x);
	string sx = tmp;
//	debug(sx.c_str());
	if(s[0] == '-') return s > sx;
	else return s < sx;
}

void judge(string s){
	if(s[0] == '0'){
		if(s.length() != 1) printf("1\n");
		else if(0 < L || 0 > R) printf("2\n");
		else printf("0\n");
		return;
	}
	if(s[0] == '-' && (s.length() == 1 || s[1] == '0')){
		printf("1\n");
		return;
	}
	if(cmp(s, L)) printf("2\n");
	else if(cmp(s, R)) printf("0\n");
	else printf("2\n");
}

int main(){
	int T;
	scanf("%lld%lld%d", &L, &R, &T);
	while(T--){
		string s;
		cin >> s;
		judge(s);
	}
	return 0;
}
