#include <iostream>
#include <string>
using namespace std;

inline long double zhuan(string s) {  //将字符串转成数字
    long double ans = 0;
    int ok;
    if (s[0] == '-') ok = -1;
    else ok = 1;
    for (int i = 0; i < s.size(); i++) {
        if (s[i] >= '0' && s[i] <= '9') {
            ans = ans * 10 + (s[i] - '0');
        }
    }
    return ans * ok;
}

int main() {
    ios::sync_with_stdio(false);
    int T;
    long long l, r;
    string x;  //用字符串读入
    cin >> l >> r >> T;
    while (T--) {
        cin >> x;
        int len = x.size();
        if (x[0] == '-') {
            if (x[1] == '0' || len == 1) {
                cout << 1 << endl;
                continue; 
            }
        }
        if (x[0] == '0' && len > 1) {
            cout << 1 << endl;
            continue;
        }
        if (len > 20) {  //超出long long范围
            cout << 2 << endl;
            continue;
        }
        long double tmp = zhuan(x);  //用long long会被卡掉第一个点。。
        if (tmp >= l && tmp <= r) {  
            cout << 0 << endl;
        } else {
            cout << 2 << endl;
        }
    }
    return 0;
}
