		if((x[0]=='-'&&x[1]=='0')||(x.size()!=1&&x[0]=='0')||(x[0]=='-'&&x.size()==1))
			cout<<"1"<<endl;
            //特判不合法的情况：1.“-0”；2.以“0”开头的数字；3.只有一个“-”
		else if(tai(x))
        //如果合法那么判断是否在[l,r]区间内
			cout<<"0"<<endl;
