然后复杂度O(n*n+q*n)

代码：
```cpp
// luogu-judger-enable-o2
#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
const ll p = 19260817;
const int N = 1005; 
ll f[N][N], fac[N], inv[N], ans;
ll pw(ll x, ll y) {
	if (!y) return 1;
	ll z = pw(x, y / 2);
	z = z * z % p;
	if (y & 1) z = z * x % p;
	return z;
}
ll C(int n, int m) {
	if (n < m) return 0;
	return fac[n] * inv[m] % p * inv[n - m] % p;
}
int main() {
	int m, n, q, i, j;
	fac[0] = inv[0] = 1;
	for (i = 1; i <= 1000; ++i) {
		fac[i] = fac[i - 1] * i % p;
		inv[i] = pw(fac[i], p - 2);
	}
	for (i = 1; i <= 1000; ++i) {
		f[i][0] = 0;
		for (j = 1; j <= 1000; ++j) f[i][j] = (f[i][j - 1] + C(i, j)) % p;
	}
	scanf("%d", &q);
	while (q--) {
		scanf("%d%d", &m, &n);
		for (i = 1, ans = 0; i <= n; ++i) (ans += f[i][m]) %= p;
		printf("%lld\n", ans);
	}
	return 0;
}

