template<typename t>void out(t a){if(a<0)putchar('-'),a=-a;if(a==0)putchar('0');while(a)buff[++pnt]=a%10+'0',a/=10;while(pnt)putchar(buff[pnt]),--pnt;putchar(mid_char);}
