signed main()
{
	int ans = 0;
	int t = read(),n = read(),l = read(),r = read(),x = read(),k = read();
	int dang = 1,zhuan = l;
	int last = 0;
	for(register int i = 1;i <= t;++ i)
	{
		int opt = read(),y = read();
		if(opt == 0)//升档 
