typedef long long ll;
typedef __int128_t lll;
typedef unsigned int ui;
typedef unsigned long long ull;

ll qpow(ll a, ll x, ll p)
{
    ll res = 1;
    for (; x; x >>= 1, a = (lll)a * a % p)
        if (x & 1)
            res = (lll)res * a % p;
    return res;
}

bool MR(ll n)
{
    if (n < 3 || n % 2 == 0)
        return n == 2;
    if (n == 3 || n == 5 || n == 7 || n == 11)
        return 1;
    if (n % 3 == 0)
        return n == 3;
    ll a = n - 1, b = 0;
    while (a % 2 == 0)
        a >>= 1, b++;
    for (int i = 0, j; i < 5; i++)
    {
        ll c = rand() % (n - 1) + 1;
        ll v = qpow(c, a, n);
        if (v == 1)
            continue;
        for (j = 0; j < b; j++)
        {
            if (v == n - 1)
                break;
            v = (lll)v * v % n;
        }
        if (j >= b)
            return false;
    }
    return true;
}

char s[100];

std::vector<int> a;

int n, m;

int check(int x)
{
    a.clear();
    int t = x - 1;
    for (int i = 2; i * i <= t; i++)
    {
        if (t % i == 0)
        {
            a.push_back(i);
            while (t % i == 0)
                t /= i;
        }
    }
    int q = x - 1;
    for (int i = 1; i <= 49; i++)
    {
        int now = i + n - 1;
        int ok = 1;
        for (int j : a)
            if (qpow(now, q / j, x) == 1)
            {
                ok = 0;
                break;
            }
        if (ok && s[i] == '.')
            return false;
        else if (!ok && s[i] == 'g')
            return false;
    }
    return true;
}

int main()
{
    int l, r;
    cin >> l >> r;
    freopen("in.in", "r", stdin);
    cin >> n >> m;
    cin >> (s + 1);
    for (int i = l; i <= r; i++)
    {
        if (!MR(i))
            continue;
        int flag = check(i);
        if (flag == 1)
            cout << i << endl;
    }
}
```

我这里采用 $10$ 个程序并行的方式进行计算，效率很高，我刚开始第六个程序，第一个程序就跑完了。

找出来合法的数只有一个：$1515343657$。质因数分解一下 $1515343656$，发现质因子是 $2,3,4003,15773$，区间不大，直接用 input14 的方法暴力判断即可。

