			rep(x, 1, t) {
				nxt = Mult(a, a, P);
				if (nxt == 1 && a != 1 && a != P-1) return false;
				a = nxt;
				if (a == 1) break;
			}
			if (a != 1) return false;
		}
		return true;
	}
