#include<map>

using namespace std;

namespace sat{	// 2-SAT

	const int maxn=1000006;
	vector<int> e[maxn*2];

	int n;
	inline int rho(int i,bool x){   // <i,x>
		return i*2-x;
	}
	void add(int i,bool a,int j,bool b){    // x[i]==a => x[j]==b
		e[rho(i,a)].push_back(rho(j,b));
		e[rho(j,b^1)].push_back(rho(i,a^1));
	}

	int dfn[maxn*2],low[maxn*2],vis[maxn*2],cnt=0;
	int scc[maxn*2],sccn=0,stk[maxn*2],tp=0;
	void dfs(int u){
		low[u]=dfn[u]=++cnt;
		stk[++tp]=u;
		vis[u]=1;
		for(int i=e[u].size()-1;i>=0;i--){
			int v=e[u][i];
			if(!dfn[v]){
				dfs(v);
				low[u]=min(low[u],low[v]);
			}else if(vis[v]){
				low[u]=min(low[u],dfn[v]);
			}
		}
		if(low[u]==dfn[u]){
			sccn++;
			while(stk[tp]!=u){
				vis[stk[tp]]=0;
				scc[stk[tp]]=sccn;
				tp--;
			}
			vis[stk[tp]]=0;
			scc[stk[tp]]=sccn;
			tp--;
		}
	}

	bool x[maxn];

	inline void clear(){
		for(int i=1;i<=2*n;i++){
			e[i].clear();dfn[i]=vis[i]=low[i]=scc[i]=stk[i]=0;
		}
		cnt=sccn=tp=n=0;
	}
	inline void init(int n_){
		clear();
		n=n_;
	}

	bool solve(){
		for(int i=1;i<=2*n;i++){
			if(!dfn[i]){ dfs(i); }
		}
		for(int i=1;i<=n;i++){
			if(scc[rho(i,0)]==scc[rho(i,1)]){
				return 0;
			}else{
				x[i]=scc[rho(i,0)]>scc[rho(i,1)];
			}
		}
		return 1;
	}

	bool& get(int i){
		return x[i];
	}

}

