#include<bits/stdc++.h>
using namespace std;
int T,n,m,c,f[55][55];
char s[55][55];
inline void input(){
	cin>>n>>m;
	c=0;
	memset(f,0,sizeof(f));
	for(int i=1;i<=n;++i)
		for(int j=1;j<=m;++j){
			cin>>s[i][j];
			if(s[i][j]=='-'||s[i][j]=='|')
				f[i][j]=++c;
		}
}
bool up(int x,int y);
bool down(int x,int y);
bool left(int x,int y);
bool right(int x,int y);
bool up(int x,int y){
	if(f[x-1][y])
		return 0;
	if(x==1||s[x-1][y]=='#')
		return 1;
	if(s[x-1][y]=='\\')
		return left(x-1,y);
	if(s[x-1][y]=='/')
		return right(x-1,y);
	return up(x-1,y);
}
bool down(int x,int y){
	if(f[x+1][y])
		return 0;
	if(x==n||s[x+1][y]=='#')
		return 1;
	if(s[x+1][y]=='\\')
		return right(x+1,y);
	if(s[x+1][y]=='/')
		return left(x+1,y);
	return down(x+1,y);
}
bool left(int x,int y){
	if(f[x][y-1])
		return 0;
	if(y==1||s[x][y-1]=='#')
		return 1;
	if(s[x][y-1]=='\\')
		return up(x,y-1);
	if(s[x][y-1]=='/')
		return down(x,y-1);
	return left(x,y-1);
}
bool right(int x,int y){
	if(f[x][y+1])
		return 0;
	if(y==m||s[x][y+1]=='#')
		return 1;
	if(s[x][y+1]=='\\')
		return down(x,y+1);
	if(s[x][y+1]=='/')
		return up(x,y+1);
	return right(x,y+1);
}
int flg[2][55][55];
inline void init(){
	memset(flg,0,sizeof(flg));
	for(int i=1;i<=n;++i)
		for(int j=1;j<=m;++j)
			if(f[i][j]){
				if(up(i,j)&&down(i,j))
					flg[0][i][j]=1;
				if(left(i,j)&&right(i,j))
					flg[1][i][j]=1;
			}
}
int o[55][55][2],p[55][55][2],q[55][55][2],t[55][55];
void UP(int x,int y,int op,int sx,int sy);
void DOWN(int x,int y,int op,int sx,int sy);
void LEFT(int x,int y,int op,int sx,int sy);
void RIGHT(int x,int y,int op,int sx,int sy);
void UP(int x,int y,int op,int sx,int sy){
	if(x==1||s[x-1][y]=='#')
		return;
	if(s[x-1][y]=='.'){
		o[x-1][y][t[x-1][y]]=op;
		p[x-1][y][t[x-1][y]]=sx;
		q[x-1][y][t[x-1][y]]=sy;
		++t[x-1][y];
		return UP(x-1,y,op,sx,sy);
	}
	if(s[x-1][y]=='\\')
		return LEFT(x-1,y,op,sx,sy);
	if(s[x-1][y]=='/')
		return RIGHT(x-1,y,op,sx,sy);
	return UP(x-1,y,op,sx,sy);
}
void DOWN(int x,int y,int op,int sx,int sy){
	if(x==n||s[x+1][y]=='#')
		return;
	if(s[x+1][y]=='.'){
		o[x+1][y][t[x+1][y]]=op;
		p[x+1][y][t[x+1][y]]=sx;
		q[x+1][y][t[x+1][y]]=sy;
		++t[x+1][y];
		return DOWN(x+1,y,op,sx,sy);
	}
	if(s[x+1][y]=='\\')
		return RIGHT(x+1,y,op,sx,sy);
	if(s[x+1][y]=='/')
		return LEFT(x+1,y,op,sx,sy);
}
void LEFT(int x,int y,int op,int sx,int sy){
	if(y==1||s[x][y-1]=='#')
		return;
	if(s[x][y-1]=='.'){
		o[x][y-1][t[x][y-1]]=op;
		p[x][y-1][t[x][y-1]]=sx;
		q[x][y-1][t[x][y-1]]=sy;
		++t[x][y-1];
		return LEFT(x,y-1,op,sx,sy);
	}
	if(s[x][y-1]=='\\')
		return UP(x,y-1,op,sx,sy);
	if(s[x][y-1]=='/')
		return DOWN(x,y-1,op,sx,sy);
	return LEFT(x,y-1,op,sx,sy);
}
void RIGHT(int x,int y,int op,int sx,int sy){
	if(y==m||s[x][y+1]=='#')
		return;
	if(s[x][y+1]=='.'){
		o[x][y+1][t[x][y+1]]=op;
		p[x][y+1][t[x][y+1]]=sx;
		q[x][y+1][t[x][y+1]]=sy;
		++t[x][y+1];
		return RIGHT(x,y+1,op,sx,sy);
	}
	if(s[x][y+1]=='\\')
		return DOWN(x,y+1,op,sx,sy);
	if(s[x][y+1]=='/')
		return UP(x,y+1,op,sx,sy);
	return RIGHT(x,y+1,op,sx,sy);
}
int tot,num,dfn[5005],low[5005],vis[5005];
stack<int> stk;
vector<int> G[5005];
void Tarjan(int u){
	dfn[u]=low[u]=++num;
	stk.push(u);
	for(int i=0,len=G[u].size();i<len;++i){
		int v=G[u][i];
		if(!dfn[v]){
			Tarjan(v);
			low[u]=min(low[u],low[v]);
		} else if(!vis[v])
			low[u]=min(low[u],dfn[v]);
	}
	if(low[u]==dfn[u]){
		vis[u]=++tot;
		while(stk.top()!=u){
			int to=stk.top();
			vis[to]=tot;
			stk.pop();
		}
		stk.pop();
	}
}
inline int F(int a,int b){
	return a+b*c;
}
inline void solve(){
	for(int i=1;i<=n;++i)
		for(int j=1;j<=m;++j)
			if(f[i][j]&&!flg[0][i][j]&&!flg[1][i][j]){
				puts("IMPOSSIBLE");
				return;
			}
	for(int i=1;i<=(c<<1);++i)
		G[i].clear();
	memset(t,0,sizeof(t));
	for(int i=1;i<=n;++i)
		for(int j=1;j<=m;++j)
			if(f[i][j]){
				if(flg[0][i][j]){
					UP(i,j,0,i,j);
					DOWN(i,j,0,i,j);
				} else
					G[F(f[i][j],0)].push_back(F(f[i][j],1));
				if(flg[1][i][j]){
					LEFT(i,j,1,i,j);
					RIGHT(i,j,1,i,j);
				} else
					G[F(f[i][j],1)].push_back(F(f[i][j],0));
			} 
	for(int i=1;i<=n;++i)
		for(int j=1;j<=m;++j)
			if(s[i][j]=='.'){
				if(t[i][j]==2){
					int op=o[i][j][0],x=p[i][j][0],y=q[i][j][0];
					int OP=o[i][j][1],X=p[i][j][1],Y=q[i][j][1];
					int a1=f[x][y],a2=f[X][Y];
					G[F(a1,op^1)].push_back(F(a2,OP));
					G[F(a2,OP^1)].push_back(F(a1,op));
				} else if(t[i][j]==1){
					int op=o[i][j][0],a=f[p[i][j][0]][q[i][j][0]];
					G[F(a,op^1)].push_back(F(a,op));	
				} else{
					puts("IMPOSSIBLE");
					return;	
				}
			}
	num=tot=0;
	memset(vis,0,sizeof(vis)); 
	memset(dfn,0,sizeof(dfn)); 
	memset(low,0,sizeof(low)); 
	while(stk.size())
		stk.pop();
	for(int i=1;i<=(c<<1);++i)
		if(!vis[i])
			Tarjan(i);
	for(int i=1;i<=c;++i)
		if(vis[i]==vis[i+c]){
			puts("IMPOSSIBLE");
			return;
		}
	puts("POSSIBLE");
	for(int i=1;i<=n;++i){
		for(int j=1;j<=m;++j){
			if(f[i][j])
				putchar(vis[f[i][j]]>vis[f[i][j]+c]?'-':'|');
			else
				putchar(s[i][j]);
		}
		puts("");
	}
}
int main(){
	cin>>T;
	while(T--){
		input();
		init();
		solve();
	}
	return 0;
}
