#include<iostream>
using namespace std;
int n,l,g;
int check(int w,int h)
{
    while(w>g || h>g)
        w/=2,h/=2;//根据规则进行处理
    if(w<l || h<l)
        return 1;//如果图片有任何一边小于L，输出 "Too Young"
    if(w!=h)
        return 2;//如果图片满足大小条件但不为正方形，输出"Too Simple"
    else
        return 3;//如果图片满足大小条件并且是正方形，输出"Sometimes Naive"

}//判断函数
int main()
{
    cin>>n>>l>>g;
    while(n--)
    {
        int w,h;
        cin>>w>>h;
        int x=check(w,h);
        if(x==1)
            cout<<"Too Young"<<endl;
        if(x==2)
            cout<<"Too Simple"<<endl;
        if(x==3)
            cout<<"Sometimes Naive"<<endl;
    }//输入，输出
    return 0;
}
```
