int n;
int main()
{
    int n,l,g;
    scanf("%d%d%d",&n,&l,&g);
    for(int i=1;i<=n;i++)
    {
        int x,y;
        scanf("%d%d",&x,&y);
        if(x>g||y>g)
        {
            while(x>g||y>g)
            {
                x=floor(x/2);
                y=floor(y/2);
            }
