	for(re i=1;i<=n;i++){
		for(re j=0;j<=m;j++){
			if(c[i][j])d[i][++t[i]][0]=(c[i][j]+c[i][j-1])*i,d[i][t[i]][1]=j;
			if(j)c[i][j]+=c[i][j-1];
		}
