#include <bits/stdc++.h>

template <class T>
inline void read(T &res)
{
	char ch;
	while (ch = getchar(), !isdigit(ch));
	res = ch ^ 48;
	while (ch = getchar(), isdigit(ch))
		res = res * 10 + ch - 48;
}

template <class T>
inline void put(T x)
{
	if (x > 9)
		put(x / 10);
	putchar(x % 10 + 48);
}

typedef long long ll;
const int N = 55;
const int M = 105;
int T, d, c;
ll a, b, s, f[N][M][2];

template <class T>
inline void CkMin(T &x, T y) {x > y ? x = y : 0;}
template <class T>
inline T Max(T x, T y) {return x > y ? x : y;}
template <class T>
inline T Min(T x, T y) {return x < y ? x : y;}

inline int askD(ll x)
{
	int cnt = 0;
	while (x)
		x >>= 1, ++cnt;
	return cnt;
}

inline ll askVal()
{
	int da = askD(a), db = askD(b);
	if (da > db)
		std::swap(a, b), std::swap(da, db);
	ll ans = 0;
	while (db > da)
	{
		ans += b;
		b >>= 1;
		--db;
	}
	while (a != b)
	{
		ans += a + b;
		a >>= 1;
		b >>= 1;
	}
	return ans + a;
}

inline int solve1()
{
	int ans = 0;
	for (int h = 2; h <= d; ++h)
	{
		ll x = s / ((1ll << h) - 1);
		if (!x)
			break ;
		if (askD(x) + h - 1 > d)
			continue ;
		ll delt = s - ((1ll << h) - 1) * x;
		for (int j = h - 1; j >= 1; --j)
			if (delt >= (1ll << j) - 1)
				delt -= (1ll << j) - 1;
		!delt ? ++ans : 0;
	}
	return ans;
}

inline ll solve2()
{
	ll res = 0;
	for (int h1 = 2; h1 <= d; ++h1)
		for (int h2 = 2; h2 <= d; ++h2)
		{
			if (s - (1ll << h2 - 1) + 1 <= 0)
				break ;
			ll x = (s - (1ll << h2 - 1) + 1) / ((1ll << h1) + (1ll << h2) - 3);
			if (!x)
				break ;
			if (askD(x) + Max(h1, h2) - 1 > d)
				continue ;
			ll delt = s - ((1ll << h1) + (1ll << h2) - 3) * x - (1ll << h2 - 1) + 1;
			int tot = h1 + h2 - 4;
			for (int cnt = 0; cnt <= tot; ++cnt)
				if (!(delt + cnt & 1))
				{
					delt += cnt;
					int ds = askD(delt) + 1;
					for (int i = 1; i <= ds + 1; ++i)
						for (int j = 0, jm = i - 1 << 1; j <= jm; ++j)
							f[i][j][0] = f[i][j][1] = 0;	
					f[1][0][0] = 1;
					for (int i = 1; i <= ds; ++i)
						for (int j = 0, jm = Min(i - 1 << 1, tot); j <= jm; ++j)
							for (int k = 0; k < 2; ++k)
								if (f[i][j][k])
								for (int a = 0, am = i < h1 - 1 ? 1 : 0; a <= am; ++a)
									for (int b = 0, bm = i < h2 - 1 ? 1 : 0; b <= bm; ++b)
										if ((k + a + b & 1) == (delt >> i & 1))
											f[i + 1][j + a + b][k + a + b >> 1] += f[i][j][k];
					res += f[ds + 1][cnt][0];
					delt -= cnt;
				}
		}
	return res;

}

int main()
{
	read(T);
	while (T--)
	{
		read(d); 
		read(a);
		read(b);
		read(c);

		s = askVal();
		put(c == 1 ? s : solve1() + solve2() - (s >= (1ll << d))), putchar('\n');
	}
}

