	cin >> d;
	int tp;
	cin >> x >> y;
	cin >> tp;
	long long tx=x,ty=y;
	while(tx!=ty){
		if(tx>ty) tx/=2;
		else ty/=2;
	}
	tx/=2;
	long long n = 2*x+2*y-2*tx-2*ty+gettmp(ty)-gettmp(x)-gettmp(y)+gettmp(tx);
	if (tp==1){
		cout << n << endl;
		continue;
	}
	for( int i = 0 ; i < d ; i++ )
		for( int j = 0 ; j < d ; j++ )
		{
			LL mul = ( 1LL << i + 1 ) + ( 1LL << j + 1 ) - 3;
			if( n < mul ) break;
			long long tmp = n/mul;
			int lenx = 0;
			while(tmp){tmp>>=1;lenx++;}
			if(lenx+i>d||lenx+j>d) continue;
			LL now = n % mul - ( 1LL << j ) + 1;
			if( now < 0 ) continue;
			int cnt = i + j;
			for( int k = now & 1 ; k <= cnt ; k += 2 )
			{
				LL tot = now + k;
				ans += solve(tot, k, i - 1, j - 1);
			}
			//cout << lenx << " " << i << " " << j << " " << ans << endl;
		}
	cout << ans-1 << endl;
