inline void modify_DWT(vector<int>&f,const int pos,const int delta){
    int n=Get(f.size()-1);
    f.resize(n);
    // printf("Now n == %d, pos == %d\n",n,pos);
    rep(i,0,n-1){
        if((i&pos)==0){
