inline int getLast(vector<int>&f){
    int ret=0,sz=f.size();
    rep(i,0,sz-1){
        ret+=f[i]*init[i];
        if(ret<0)ret+=MOD;
        else if(ret>=MOD)ret-=MOD;
    }return ret;
}

