```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
using namespace std;
typedef long long ll;
#define N 800080
const int mod=167772161;
const int G=3;
int n,k,tr[N];
int f[N],g[N],GG[2][20][N],inv[N],fac[N];
int qpow(int a,int b){
	int ans=1;
	while(b){
		if(b&1)ans=(ll)ans*a%mod;
		a=(ll)a*a%mod;
		b>>=1; 
	}
	return ans; 
}
const int invG=qpow(G,mod-2);
void init_GG(){
	for(int p=1;p<=19;++p){
		int buf0=qpow(G,(mod-1)/(1<<p));
		int buf1=qpow(invG,(mod-1)/(1<<p));
		GG[0][p][0]=GG[1][p][0]=1;
		for(int i=1;i<(1<<p);++i){
			GG[0][p][i]=(ll)GG[0][p][i-1]*buf0%mod;
			GG[1][p][i]=(ll)GG[1][p][i-1]*buf1%mod;
		}
	}
}
void init_QAQ(int n){
	fac[0]=1;
	for(int i=1;i<=n;++i){
		fac[i]=(ll)fac[i-1]*i%mod;
	}
	inv[n]=qpow(fac[n],mod-2)%mod;
	for(int i=n-1;i>=0;--i){
		inv[i]=(ll)inv[i+1]*(i+1)%mod;
	}
}
void NTT_init(int n){
	for(int i=0;i<n;++i){
		tr[i]=((tr[i>>1]>>1)|((i&1)?n>>1:0));
	}
}
#define ck(x) (x>=mod?x-mod:x)
void NTT(int *f,int flag,int n){
	for(int i=0;i<n;++i){
		if(i<tr[i])swap(f[i],f[tr[i]]);
	}
	for(int p=2,j=1;p<=n;p<<=1,++j){
		int len=p>>1;
		for(int k=0;k<n;k+=p){
			int *buf=GG[flag][j];
			for(int i=k;i<k+len;++i,++buf){
				int tmp=(ll)(*buf)*f[i+len]%mod;
				f[i+len]=ck(f[i]-tmp+mod);
				f[i]=ck(f[i]+tmp);
			}
		}
	}
	if(!flag){
		int invn=qpow(n,mod-2);
		for(int i=0;i<n;++i){
			f[i]=(ll)f[i]*invn%mod;
		}
	}
}
void WeiFen(int *f,int *g,int n){
	for(int i=1;i<n;++i){
		g[i-1]=(ll)f[i]*i%mod;
	}
	g[n-1]=0;
}
void JiFen(int *f,int *g,int n){
	for(int i=1;i<n;++i){
		g[i]=(ll)f[i-1]*qpow(i,mod-2)%mod;
	}
	g[0]=0;
}
void Mul(int *_f,int *_g,int *p,int n,int m){
	static int f[N],g[N];
	int lim=1;
	while(lim<n+m)lim<<=1;
	NTT_init(lim);
	for(int i=0;i<n;++i)f[i]=_f[i];
	for(int i=0;i<m;++i)g[i]=_g[i];
	for(int i=n;i<lim;++i)f[i]=0;
	for(int i=m;i<lim;++i)g[i]=0;
	NTT(f,1,lim),NTT(g,1,lim);
	for(int i=0;i<lim;++i){
		p[i]=(ll)f[i]*g[i]%mod;
	}
	NTT(p,0,lim);
}
void Inv(int *f,int *g,int m){
	if(m==1){
		g[0]=qpow(f[0],mod-2);
		return;
	}
	Inv(f,g,(m+1)>>1);
	static int w[N];
	int lim=1;
	while(lim<(m<<1))lim<<=1;
	NTT_init(lim);
	for(int i=0;i<m;++i){
		w[i]=f[i];
	}
	for(int i=m;i<lim;++i)w[i]=0;
	NTT(w,1,lim),NTT(g,1,lim);
	for(int i=0;i<lim;++i){
		g[i]=(2-(ll)w[i]*g[i]%mod+mod)*g[i]%mod;
	}
	NTT(g,0,lim);
	for(int i=m;i<lim;++i)g[i]=0;
}
void Ln(int *f,int *g,int n){
	static int _f[N],_g[N],ff[N];
	for(int i=0;i<(n<<2);++i)_f[i]=_g[i]=ff[i]=0;
	WeiFen(f,_f,n);
	Inv(f,ff,n);
	Mul(_f,ff,_g,n,n);
	JiFen(_g,g,n);
}
void Exp(int *f,int *g,int m){
	if(m==1){
		g[0]=1;
		return;
	}
	Exp(f,g,(m+1)>>1);
	static int w[N],lng[N];
	for(int i=0;i<(m<<2);++i)lng[i]=0;
	Ln(g,lng,m);
	for(int i=0;i<m;++i){
		w[i]=f[i];
	}
	int lim=1;
	while(lim<(m<<1))lim<<=1;
	NTT_init(lim); 
	for(int i=m;i<lim;++i)w[i]=0;
	NTT(g,1,lim),NTT(lng,1,lim),NTT(w,1,lim);
	for(int i=0;i<lim;++i){
		g[i]=(ll)(1-lng[i]+w[i]+mod)*g[i]%mod;
	}
	NTT(g,0,lim);
	for(int i=m;i<lim;++i)g[i]=0;
}
void Pow(int *_f,int *g,int n,int m){
	static int f[N],lnf[N];
	int dig=0;
	while(!_f[dig])++dig;
	if(1LL*dig*m>=n)return;
	n-=dig;
	for(int i=0;i<n;++i){
		f[i]=_f[i+dig];
	}
	int _0=f[0],inv0=qpow(_0,mod-2);
	for(int i=0;i<n;++i){
		f[i]=(ll)f[i]*inv0%mod;
	}
	Ln(f,lnf,n);
	for(int i=0;i<n;++i){
		lnf[i]=(ll)lnf[i]*k%mod;
	}
	Exp(lnf,g,n);
	dig*=m;
	for(int i=n+dig-1;i>=dig;--i){
		g[i]=(ll)g[i-dig]*qpow(_0,k-1)%mod;
	}
	for(int i=dig-1;i>=0;--i){
		g[i]=0;
	}
}
int main(){
	n=read()+1,k=read();
	init_GG();
	init_QAQ(max(n,k));
	for(int i=1;i<n;++i){
		f[i]=inv[i];
	}
	Pow(f,g,n,k);
	for(int i=0;i<n;++i){
		printf("%d ",(ll)g[i]*fac[i]%mod*inv[k]%mod);
	}
	return 0;
}

