int main()
{
	n=read(),m=read();blo=sqrt(n);S=100;
	for(int i=1;i<=n;i++)	a[i]=read(),bl[i]=(i-1)/blo+1,mx=max(a[i],mx);
	for(int i=1;i<=n;i++)
	{
		if(fac[a[i]].empty())
		{
			for(int j=1;j*j<=a[i];j++)
				if(a[i]%j==0)
				{
					f[i]+=cnt[j],flg[j]++;
					if(j*j!=a[i])	f[i]+=cnt[a[i]/j],flg[a[i]/j]++;
					fac[a[i]].emplace_back(j);
				}
		}
		else
