			}
		}
		if(r>q[i].r)
		{
			d[++JSS]={l-1,q[i].r+1,r,2,i};
			while(r>q[i].r)
			{
				q[i].ans-=ans1[r];
				--r;
			}
		}
		if(l<q[i].l)
		{
			d[++JSS]={r,l,q[i].l-1,3,i};
			while(l<q[i].l)
			{
				q[i].ans+=ans2[l];
				++l;
			}
		}
	}
	for(int i=1;i<=MAXN;++i)
	ton[i]=ton2[i]=0;
	int zz=1;
	sort(d+1,d+JSS+1,cmp4);
	while(zz<=JSS&&d[zz].wz==0)
	++zz;
	for(int i=1;i<=n;++i)
	{
		++ton[num[i]];
		for(int j=0;j<D[num[i]].size();++j)
		{
			int v=D[num[i]][j];
			++ton2[v];
		}
		if(num[i]>s2)
		{
			for(int j=1;num[i]*j<=MAXN;++j)
			++ton2[num[i]*j];
		}
		while(zz<=JSS&&d[zz].wz==i)
		{
			int l=d[zz].l,r=d[zz].r,kind=d[zz].kind,dat=d[zz].dat;
			for(int k=l;k<=r;++k)
			{
				if(kind==0||kind==3)
				q[dat].ans-=ton2[num[k]];
				else
				q[dat].ans+=ton2[num[k]];
			}
			++zz;
		}
	}
	for(int i=1;i<=s2;++i)
	{
		for(int j=1;j<=n;++j)
		sum[j]=sum[j-1]+!(num[j]%i);

		long long k=0;
		int zz=1;
		while(zz<=JSS&&d[zz].wz==0)
		++zz;
		for(int j=1;j<=n;++j)
		{
			if(num[j]==i)
			++k; 
			while(zz<=JSS&&d[zz].wz==j)
			{
				int l=d[zz].l,r=d[zz].r,kind=d[zz].kind,dat=d[zz].dat;
				if(kind==0||kind==3)
				q[dat].ans-=k*(sum[r]-sum[l-1]);
				else
				q[dat].ans+=k*(sum[r]-sum[l-1]);
				++zz;
			}

		}
	}
	for(int i=1;i<=m;++i)
	q[i].ans+=q[i-1].ans,ans[q[i].dat]=q[i].ans+q[i].r-q[i].l+1;
	for(int i=1;i<=m;++i)
	write(ans[i]),putchar('\n');
	flush();
	return 0;
}
