			dr[i]+=(pre[i][9]-pre[i-1][9])*t[l+9];
			if(a[i]<=sz) ++t[a[i]];
			for(int j=vr[i];j;j=er[j].nxt){
				x=er[j].y;
				u=p[x][0],v=p[x][1];
				del[x]+=t[l]*1ll*(pre[v][0]-pre[u-1][0]);
