#include<bits/stdc++.h>//万能头文件
#include<queue>
using namespace std;
int n;
int a[10005];//存性别
double b[10005];//存身高
priority_queue<double,vector<double>,greater<double> > q1;//因为懒，就设了两个队列
priority_queue<double,vector<double>,greater<double> > q2;
int main()
{
	int k;
	cin>>k;//数据组数
	for(int i=1;i<=k;i++)
	{
		n=0;
        memset(a,0,sizeof(a));
		memset(b,0,sizeof(b));//每次进行一轮都要进行初始化！
		cin>>n;
		for(int j=1;j<=n;j++)
		cin>>a[j];
		for(int j=1;j<=n;j++)
		cin>>b[j];
		for(int j=1;j<=n;j++)
		{
			if(!a[j]) q1.push(b[j]);//女生就压入q1
			else q2.push(b[j]);	//男生就压入q2
		}
		while(!q1.empty())//先将女生数据弹出
		{
			double a=q1.top();
			cout<<a<<" ";
			q1.pop();
		}
		cout<<endl;//记得回车
		while(!q2.empty())//后将男生数据弹出
		{
			double a=q2.top();
			cout<<a<<" ";
			q2.pop();
		}
		cout<<endl;
	} 
	return 0;//带功告成
} 
```
