int n,mod,k;
inline int fast(int x,int b){
	int ans=1,base=x%mod;
	while(b){
		if(b&1)ans=ans*base%mod;
		base=base*base%mod;
		b>>=1;
	}
	return ans%mod;
}
int a[N],sum[N],inv[N],sinv;
