		sum[i]=sum[i-1]*a[i]%mod;
	}
	sinv=fast(sum[n],mod-2);
	for(int i=n;i>=1;i--){
		inv[i]=sum[i-1]*sinv%mod;//a[i]的逆元等于s[i-1]*(s[i]的逆元) 
		sinv=sinv*a[i]%mod;//变成s[i-1]的逆元 
	}
