#include<bits/stdc++.h>
#define ll long long
using namespace std;
inline int read(){
	char c=getchar();int x=0,f=1;
    while(c<'0'||c>'9'){if(c=='-')f=-1;c=getchar();}
    while(c>='0'&&c<='9'){x=x*10+c-'0';c=getchar();}
    return x*f;
}
int i,j,k,n,m;
const int maxn=5010,N=1000000;
int a[maxn],b[maxn],s[maxn][3];
int prime[N+10],is_prime[N+10],num;
void init(){
	for(int i=2;i<=N;i++)is_prime[i]=1;
	for(int i=2;i<=N;i++){
//		cout<<"i="<<i<<' '<<is_prime[i]<<endl;
		if(is_prime[i])prime[++num]=i;
		for(int j=1;j<=num && i*prime[j]<=N;j++){
			is_prime[prime[j]*i]=0;
			if(i%prime[j]==0)break;
		}
	}
}
void make(int now,int num){
	s[now][0]=s[now][1]=s[now][2]=1;
	int T=0,nn=sqrt(b[now]);
	for(int i=1;i<=num;i++){
		if(num<prime[i]*prime[i])break;
		while(num%prime[i]==0){
			if(s[now][T]*prime[i]>nn)++T;
			s[now][T]*=prime[i];
			num/=prime[i];
		}
	}
	if(s[now][T]*num>nn)++T;
	s[now][T]*=num;
	num/=num;
	sort(s[now],s[now]+3);
}
int Gcd,X;
int gcd(int x,int y){
	return y==0?x:gcd(y,x%y);
}
int GCD[1010][1010],B_[maxn];
void Dogcd(int now){
	int B=B_[now];
	for(int i=0;i<=2;i++){
		if(s[now][i]>B){
			if(X%s[now][i]==0)
				X/=s[now][i],Gcd*=s[now][i];
		}else{
//			cout<<s[now][i]<<' '<<X<<endl;
			int tmp=GCD[s[now][i]][X%s[now][i]];
			X/=tmp;Gcd*=tmp;
		}
	} 
}
const ll mod=998244353;
int main(){
	init();
	for(i=0;i<=1000;i++)
		for(j=i;j<=1000;j++)
			GCD[i][j]=GCD[j][i]=gcd(i,j);
	cin>>n;
	for(i=1;i<=n;i++)a[i]=read();
	for(i=1;i<=n;i++)b[i]=read();
	for(i=1;i<=n;i++)make(i,b[i]);
	ll ans=0;
	for(i=1;i<=n;i++)B_[i]=sqrt(b[i]);
	for(i=1;i<=n;i++){
		ans=0;
		ll S=1;
		for(j=1;j<=n;j++){
			S=S*i%mod;
//			cout<<i<<' '<<j<<' '<<a[i]<<' '<<b[j]<<"="<<s[j][0]<<"*"<<s[j][1]<<"*"<<s[j][2]<<endl;
			Gcd=1;X=a[i];
			Dogcd(j);
			ans+=S*Gcd%mod;
		}
		printf("%lld\n",ans%mod);
	}
	return 0;
}
