		for( int j = 1 ; j <= prnum && 1ll * i * prime[ j ] <= MAXN ; j ++ ) {
			vis[ i * prime[ j ] ] = 1;
			Sk[ i * prime[ j ] ] = Mul( Sk[ i ] , Sk[ prime[ j ] ] );
			if( i % prime[ j ] == 0 ) break;
		}
	}
	for( int i = 1 ; i <= MAXN ; i ++ ) Sk[ i ] = Add( Sk[ i ] , Sk[ i - 1 ] ); 
