#include<iostream>
#include<iomanip>
#include<cstring>
#include<cmath>
#include<cstdio>
#include<queue>
#include<algorithm>
using namespace std;
int t,p,n;
long long f[50005],g[50005],sum[50005];
long long ksm(long long x,int n)
{
	long long ans=1;
	while(n)
	{
		if(n&1) ans=ans*x%p;
		x=x*x%p;
		n>>=1;
	}
	return ans;
}
int main()
{
	scanf("%d%d",&t,&p);
	f[0]=f[1]=1,g[1]=1;
	f[2]=2,g[2]=1;
	for(int i=1;i<=23333;i++) sum[i]=1;
	for(int i=2;i<=23333;i+=2) sum[i]+=2;
	for(long long i=3;i<=23333;i++)
	{
		__int128 tmp=0;
		for(int j=0;j<i;j++) tmp+=f[j]*sum[i-j];
		tmp%=p;
		g[i]=tmp*ksm(i,p-2)%p;
		f[i]=g[i]*2%p;
		for(int j=i;j<=23333;j+=i) sum[j]+=tmp,sum[j]%=p;
	}
	while(t--)
	{
		scanf("%d",&n);
		printf("%lld\n",f[n]);
	}
}
