		while(p<0)p+=e2;
		while(q>0)q-=e1;
		lxl inv,y;
		exGCD(c2,n,inv,y);
		inv=(inv%n+n)%n;
		q*=-1;
		printf("%lld\n",FastMul(FastPow(c1,p,n),FastPow(inv,q,n),n));
	}
	return 0;
}
