  exgcd(e1,e2,x,y);
  auto ksm=[&](i128 a,i128 b,i128 p){
    i128 ans=1;
    for(;b;b>>=1,a=a*a%p)
    if(b&1)ans=ans*a%p;
    return ans;
  };
  if(x<0)x=-x,c1=inv(c1,N);
  if(y<0)y=-y,c2=inv(c2,N);
  cout<<(i64)(ksm(c1,x,N)*ksm(c2,y,N)%N)<<endl;
