int exgcd(int& x,int& y,int a,int b)
{
	if(!b)	{
		x=1,y=0;
		return a;
	}
	int d=exgcd(x,y,b,a%b);
	int z=x;x=y;y=z-(a/b)*y;
	return d;
}
inline int inv(int x)	{
	int t1=0,t2=0;
	exgcd(t1,t2,x,n);
	return (t1%n+n)%n;
}
inline int mul(int a,int t)	{
	int ans=0;
	while(t)	{
		if(t&1) ans+=a,ans%=n;
		a<<=1,a%=n;
		t>>=1;
	}
	return ans%n;
}
inline int Qpow(int a,int t)	{
	int ans=1;
	while(t)	{
		if(t&1) ans=mul(ans,a);
		a=mul(a,a);
		t>>=1;
	}
	return ans%n;
}
