                    goal--;
                    while(flag[goal])goal--;
                    break;
                }
                q.push(a[i]);//进队
                while(!a[i+vis])vis++;
              //如果为0说明已经被取出，继续先后跳
                q.push(a[i+vis]);//进队
                flag[a[i]]=1,flag[a[i+vis]]=1;
                while(flag[goal])goal--;
                a[i]=0,a[i+vis]=0;
