struct tt{
    int x,ls;//x是次龙珠序号，ls是此龙珠后的序号
    bool friend operator <(tt xx,tt yy){
        if(xx.ls>n)return 1;
        if(yy.ls>n)return 0;
        //细节处理，以为如果此元素在队末，就无法取走
        return a[xx.x]<a[yy.x]; //排序，按编号
    }
