        dl.push((tt){i,i+1});//入队
    }
    u=0;b[0]=b[n+1]=1;
    while(!dl.empty()){
        tt p=dl.top();dl.pop();
        if(b[p.x]||b[p.ls])continue;//非法元素
        tot[u+1]=a[p.x];tot[u+2]=a[p.ls];u+=2;//入答案队列
        b[p.x]=b[p.ls]=1;//标记已取走的龙珠，便于判断非法元素
        if(sd[p.x]==0)continue;
        sa[sd[p.x]]=sa[p.ls];sd[sa[p.ls]]=sd[p.x];//修改链表
        dl.push((tt){sd[p.x],sa[p.ls]});//加入新的正确元素
    }
