更多理解和实现在代码里

```cpp
#include<bits/stdc++.h>
using namespace std;
int n,m,a[100005],maxx,w[100005],t[100005],nex[100005],v[100005];
//a是元素，w记录位置，t表示能不能选，nex是链表，v是已经选了那些
//其实v和t用处不一样，见后文 
int main()
{
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		scanf("%d",&a[i]);
		nex[a[i-1]]=a[i];
		w[a[i]]=i;//简单的预处理 
	}
	a[n+1]=a[n+2]=1e9;
	int maxx=n;//maxx是贪心中的最大值 
	m=n+2;
	int jw=n;//确定哪一个点为末尾 
	t[a[maxx]]=1;//不能选最后 
	while(m-=2)//丑陋的循环 
	{
		while(t[maxx]==1)
		maxx--;//找到合法最大值 
		int u=w[maxx];//确定位置 
		t[maxx]=1;
		t[nex[maxx]]=1;
		v[maxx]=1;
		v[nex[maxx]]=1;//双双标记 
		while(v[a[jw]]==1)
		jw--;//确定末尾，注意不能用t来判 
		t[a[jw]]=1;
		cout<<maxx<<" "<<nex[maxx]<<' ';
		int hh=u,uu=u;
		while(uu>0&&v[a[uu-1]]==1)//找到最前面的没选的值 
		{
			uu--;
		}
		while(u+2<=n&&v[a[u+2]]==1)//最后没选的合法值 
		{
			u++;
		}
		nex[a[uu-1]]=a[u+2];//维护链表 
	}
	return 0;
}
