#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>
#include<set>
using namespace std;
int n,a[100005],v[100005],nx,tot;
int main()
{
    int i,j,x,y;
    set<int> f,f1,f2;
    scanf("%d",&n);
    for(i=1;i<=n;i++)
    {
    	scanf("%d",&a[i]);
    	v[a[i]]=i;
    	f1.insert(-a[i]);//插入负数以求最大值
    	f.insert(i);
    	f2.insert(-i);
    }
    while(!f.empty())
    {
        x=-*f1.lower_bound(-n);
        if(v[x]==-*f2.lower_bound(-n))//维护边界
        x=-*f1.upper_bound(-x);
        nx=*f.upper_bound(v[x]);//后一个数
        f1.erase(-x);
        f1.erase(-a[nx]);
        f.erase(v[x]);
        f.erase(nx);
        f2.erase(-v[x]);
        f2.erase(-nx);
        printf("%d %d ",x,a[nx]);
