}; 

namespace task3 {   
    int sta[N],sum[N],prev[N];  
    queue<int>q;  
    void solve(int n,int m) {      
        int MAX=n-1;                
        for(int i=0;(1<<i)<=MAX;++i) {  
            for(int j=0;j<=MAX;++j) {   
                if(j&(1<<i)) modify(j);  
            }       
            for(int j=0;j<=MAX;++j) { 
                int p=query(j); 
