        modify(0);  
        for(int i=1;i<n;++i) { 
            int p=query(i);  
            if(p!=sta[i]) {      
                q.push(i),report(0,i);             
            }
        }    
        while(!q.empty()) {     
            int u=q.front(); q.pop();   
            int aft=sum[u]^prev[u]^u;   
            if(aft) {   
                report(u,aft);  
                prev[aft]=u;    
                q.push(aft);   
            }
        }
    }
};   

namespace taskB {  
