            for(int i=0;i<a.size();++i) { 
                report(a[i],l);  
            }  
            return; 
        }  
        int mid=(l+r)>>1;     
        vector<int>pl,pr;  
        for(int i=l;i<=mid;++i) {    
            modify(i);   
        }                                       
        for(int i=mid+1;i<=r;++i) {
            int cur=query(i); 
            if(cur) pl.pb(i);     
        }   
        for(int i=0;i<a.size();++i) { 
            int cur=query(a[i]);  
            if(cur) pl.pb(a[i]); 
            else pr.pb(a[i]);  
        } 
        for(int i=l;i<=mid;++i) { 
            modify(i);  
        }
        work(l,mid,pl); 
        work(mid+1,r,pr);   
